import {Injectable} from '@angular/core';
import {Product} from "./products";

@Injectable({
    providedIn: 'root'
})
export class CartService {

    items: Product[] = [];

    constructor() {
}

    addToCart(product: Product) {
        this.items.push(product);
    }

    getItems() {
        return this.items;
    }

    clearCart() {
        this.items = [];
        return this.items;
    }

    clearProductFromCart(product: Product) {
        this.items = this.items.filter(product => product.id != product.id);
        return this.items;
    }
}
